mapper.select:
select
i.deal_price ORDER_PRICE, s.price CURRENT_PRICE from sj_tb_order o
left join sj_tb_order_items i on o.id = i.order_id
left join sj_tb_goods_sku s on i.target_id = s.id
where i.target_type = 'sku' and o.order_num = #{orderNum,jdbcType=VARCHAR}
and s.sku_num = #{skuCode,jdbcType=VARCHAR}

select s.price
CURRENT_PRICE from sj_tb_order o
left join sj_tb_order_items i on o.id = i.order_id
left join sj_tb_goods_sku s on i.target_id = s.id
where i.target_type = 'sku' and o.order_num = #{orderNum,jdbcType=VARCHAR}
and s.sku_num = #{skuCode,jdbcType=VARCHAR}

select r.region_id area from sj_tb_members m join
sj_buzmgt.sys_registdata r on r.member_id=m.id where
m.username=#{username}

SELECT r.username, r.name, r.create_time FROM (SELECT r.*, ROWNUM RN FROM ( SELECT m.username, g.name, r.create_time from sj_tb_user_goods_record r join sj_tb_members m on r.user_id = m.id join sj_tb_goods g on r.goods_num = g.goods_num where 1=1
and m.username=#{userName,jdbcType=VARCHAR}
and g.name like '%${goodsName}%'
and to_char(r.create_time,'yyyy-mm-dd hh24:mi') between #{startTime,jdbcType=VARCHAR} and #{endTime,jdbcType=VARCHAR}
order by r.create_time desc ) r WHERE ROWNUM <= #{page}*#{rows}) r WHERE RN >= (#{page}-1)*#{rows}

select count(r.id) count from sj_tb_user_goods_record r join sj_tb_members m on r.user_id = m.id join sj_tb_goods g on r.goods_num = g.goods_num where 1=1
and m.username=#{userName,jdbcType=VARCHAR}
and g.name like '%${goodsName}%'
and to_char(r.create_time,'yyyy-mm-dd hh24:mi') between #{startTime,jdbcType=VARCHAR} and #{endTime,jdbcType=VARCHAR}
